  println "[[${services.localization.render('glossary.deepl.explorer.forceSync')}>>Scheduler.WebHome||queryString=\"do=trigger&which=Glossary.Code.DeepL.SynchronizeGlossaries&form_token=${services.csrf.getToken()}\"]]"

