    println "|=${services.localization.render('glossary.deepl.explorer.glossaryId')}|=${services.localization.render('glossary.deepl.explorer.glossaryName')}|=${services.localization.render('glossary.deepl.explorer.sourceLanguage')}|=${services.localization.render('glossary.deepl.explorer.targetLanguage')}|=${services.localization.render('glossary.deepl.explorer.isReady')}|=${services.localization.render('glossary.deepl.explorer.entryCount')}"
    services.glossary.deepl.GetDeeplGlossariesEntries().each { glossary ->
