        }

    println "=${services.localization.render('glossary.deepl.explorer.supportedLanguagePairs')}="
    println "|=${services.localization.render('glossary.deepl.explorer.sourceLanguage')}|=${services.localization.render('glossary.deepl.explorer.targetLanguage')}"
